import React, { useState } from 'react';
import { Avatar, Button, Paper, Grid, Typography, Container, TextField } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import useStyles from './styles';
import Input from './Input';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { FcGoogle } from 'react-icons/fc';
import { AUTH } from '../../constants/actionTypes';
import { auth, provider, signInWithPopup } from '../Firebase/firebase';
import axios from 'axios';

const initialState = { firstName: '', lastName: '', email: '', password: '', confirmPassword: '' };

const Auth = () => {
    const classes = useStyles();
    const [showPassword, setShowPassword] = useState(false);
    const [isSignup, setIsSignup] = useState(false);
    const [formData, setFormData] = useState(initialState);
    const [isTelegramAuth, setIsTelegramAuth] = useState(false);
    const [verificationCode, setVerificationCode] = useState('');
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleShowPassword = () => setShowPassword((prevShowPassword) => !prevShowPassword);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (isTelegramAuth) {
            try {
                const response = await axios.post('/user/verify-telegram-code', { code: verificationCode });
                if (response.data.success) {
                    dispatch({ type: AUTH, data: { result: response.data.user, token: response.data.token } });
                    navigate('/');
                } else {

                    alert('Invalid verification code.');
                }
            } catch (error) {
                console.error('Error verifying Telegram code:', error);
            }
        } else {
            // Existing Google sign-in or other sign-in logic
        }
    };

    const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const switchMode = () => {
        setIsSignup((prevIsSignup) => !prevIsSignup);
        setShowPassword(false);
    };

    const googleSignIn = () => {
        signInWithPopup(auth, provider)
            .then(async (result) => {
                const user = result.user;
                const token = await user.getIdToken();
                const profile = {
                    name: user.displayName,
                    email: user.email,
                    picture: user.photoURL,
                };

                try {
                    dispatch({ type: AUTH, data: { result: profile, token } });
                    navigate('/');
                } catch (error) {
                    console.log(error);
                }
            })
            .catch((error) => {
                console.log('Google Sign In was unsuccessful. Try again later');
                console.log(error);
            });
    };

    const startTelegramAuth = async () => {
        try {
            const response = await axios.post('/user/start-telegram-auth', { phone_number: formData.phone_number });
            console.log('Telegram Auth Started:', response.data);
            // Redirect to Telegram bot and start the authentication process
            window.open('https://t.me/regnodejs_bot', '_blank');
            setIsTelegramAuth(true); // Indicate that the Telegram auth process has started
        } catch (error) {
            console.error('Error starting Telegram auth:', error);
        }
    };

    return (
        <Container component="main" maxWidth="xs">
            <Paper className={classes.paper} elevation={3}>
                <Avatar className={classes.avatar}>
                    <LockOutlinedIcon />
                </Avatar>
                <Typography style={{ color: 'white' }} component="h1" variant="h5">
                    {isSignup ? 'Sign up' : isTelegramAuth ? 'Enter Telegram Code' : 'Sign in'}
                </Typography>
                <form className={classes.form} onSubmit={handleSubmit}>
                    <Grid container spacing={2}>
                        {isSignup && (
                            <>
                                <Input name="firstName" label="First Name" handleChange={handleChange} autoFocus half />
                                <Input name="lastName" label="Last Name" handleChange={handleChange} half />
                            </>
                        )}
                        {isTelegramAuth ? (
                            <Grid item xs={12}>
                                <TextField
                                    name="verificationCode"
                                    label="Verification Code"
                                    fullWidth
                                    value={verificationCode}
                                    onChange={(e) => setVerificationCode(e.target.value)}
                                    variant="outlined"
                                    InputProps={{ style: { color: '#d5d5d5' } }}
                                    InputLabelProps={{ style: { color: '#d5d5d5' } }}
                                />
                            </Grid>
                        ) : (
                            <>
                                <Input name="email" label="Email Address" handleChange={handleChange} type="email" />
                                <Input name="password" label="Password" handleChange={handleChange} type={showPassword ? 'text' : 'password'} handleShowPassword={handleShowPassword} />
                                {isSignup && <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange} type="password" />}
                            </>
                        )}
                    </Grid>
                    <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>
                        {isSignup ? 'Sign Up' : isTelegramAuth ? 'Verify Code' : 'Sign In'}
                    </Button>
                    <Grid container justify="flex-end">
                        <Grid item>
                            <Button style={{ color: '#d5d5d5' }} onClick={switchMode}>
                                {isSignup ? 'Already have an account? Sign in' : "Don't have an account? Sign Up"}
                            </Button>
                        </Grid>
                    </Grid>
                </form>
            </Paper>
        </Container>
    );
};

export default Auth;
