import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      '& .MuiOutlinedInput-root': {
        '& fieldset': {
          borderColor: 'white', // White outline color
        },
        '&:hover fieldset': {
          borderColor: 'white', // White outline color on hover
        },
        '&.Mui-focused fieldset': {
          borderColor: 'white', // White outline color when focused
        },
        color: 'white', // White text color
      },
      '& .MuiInputLabel-root': {
        color: 'white', // White label color
      },
      '& .MuiInputBase-input': {
        color: 'white', // White text color
      },
    },
  },
  paper: {
    padding: theme.spacing(2),
    borderRadius: '15px', // Rounded corners for a modern look
    boxShadow: '0 16px 32px rgba(0, 0, 0, 0.15)', // Subtle shadow for a "floating" effect
    margin: '20px 0', // Margin to separate the form from other elements
  },
  form: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    background: 'transparent',
  },
  fileInput: {
    width: '97%',
    margin: '10px 0',
    color: 'white', // White text color for file input
    '& .MuiInputBase-root': {
      color: 'white', // White text color for the input label
    },
  },
  buttonSubmit: {
    marginBottom: 10,
    backgroundColor: '#007bff', // Custom blue color for the submit button
    color: '#fff',
    '&:hover': {
      backgroundColor: '#0056b3', // Darker blue on hover
    },
  },
  buttonClear: {
    backgroundColor: '#ff4d4d', // Custom red color for the clear button
    color: '#fff',
    '&:hover': {
      backgroundColor: '#cc0000', // Darker red on hover
    },
  },
}));
