export default makeStyles({
  title: {
    padding: '0 16px',
    color: '#ff5722', // Change this to remove blue color from the title
  },
  tags: {
    color: '#3f51b5', // If the tags are blue, change this to your desired color
  },
  likeButton: {
    color: '#ff5722', // Ensure this isn't blue if you want a different color
  },
  deleteButton: {
    color: '#f44336', // Change this if you need a different color
  },
});
