import React, { useState } from 'react';

const Register = () => {
    const [formData, setFormData] = useState({
        name: '',
        password: '',
        phone: '',
    });
    const [showVerification, setShowVerification] = useState(false);
    const [inputCode, setInputCode] = useState('');
    const [serverCode, setServerCode] = useState(''); // Simulating the code from the server

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const formatDate = (dateString) => {
        const parts = dateString.split('.');
        return `${parts[2]}-${parts[1]}-${parts[0]}`;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formattedDate = formatDate(formData.dob);

        try {
            const response = await fetch('http://localhost:5000/auth', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    name: formData.name,
                    password: formData.password,
                    phone_number: formData.phone,
                }),
            });
            const data = await response.json();
            if (response.ok) {
                console.log('Registration Success:', data);
                setServerCode(data.verificationCode); // Save the server-side generated code
                setShowVerification(true); // Move to the verification step
            } else {
                throw new Error(data.message || 'Registration failed');
            }
        } catch (error) {
            console.error('Registration Error:', error);
            alert(error.message);
        }
    };

    const handleVerification = () => {
        if (inputCode === serverCode) {
            alert('Verification successful!');
        } else {
            alert('Incorrect verification code. Please try again.');
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#f0f4f8' }}>
            {!showVerification ? (
                <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', width: '300px', padding: '20px', borderRadius: '8px', backgroundColor: '#fff', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
                    {/* Registration form fields */}
                    <input type="text" name="name" placeholder="Enter your name" value={formData.name} onChange={handleChange} style={inputStyle} />
                    <input type="text" name="surname" placeholder="Enter your surname" value={formData.surname} onChange={handleChange} style={inputStyle} />
                    <input type="text" name="dob" placeholder="Enter your date of birth" value={formData.dob} onChange={handleChange} style={inputStyle} />
                    <input type="email" name="email" placeholder="Enter your email" value={formData.email} onChange={handleChange} style={inputStyle} />
                    <input type="tel" name="phone" placeholder="Enter your phone number" value={formData.phone} onChange={handleChange} style={inputStyle} />
                    <button type="submit" style={buttonStyle}>Register</button>
                </form>
            ) : (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <label style={{ marginBottom: '10px' }}>Enter verification code:</label>
                    <input type="text" placeholder="Verification Code" value={inputCode} onChange={(e) => setInputCode(e.target.value)} style={inputStyle} />
                    <button onClick={handleVerification} style={buttonStyle}>Verify</button>
                </div>
            )}
        </div>
    );
};

const inputStyle = {
    marginBottom: '10px',
    padding: '10px',
    borderRadius: '4px',
    border: '1px solid #ccc',
};

const buttonStyle = {
    padding: '10px',
    borderRadius: '4px',
    backgroundColor: '#007bff',
    color: '#fff',
    cursor: 'pointer',
};

export default Register;
