import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  '@keyframes animate': {
    '0%': {
      '--a': '0deg',
    },
    '100%': {
      '--a': '360deg',
    },
  },
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      '& .MuiOutlinedInput-root': {
        '& fieldset': {
          borderColor: 'white',
        },
        '&:hover fieldset': {
          borderColor: 'white',
        },
        '&.Mui-focused fieldset': {
          borderColor: 'white',
        },
        color: 'white',
      },
      '& .MuiInputLabel-root': {
        color: 'white',
      },
      '& .MuiInputBase-input': {
        color: 'white',
      },
    },
  },
  paper: {
    padding: theme.spacing(2),
    backgroundColor: 'transparent',
    borderRadius: '15px',
    boxShadow: '0 16px 32px rgba(0, 0, 0, 0.15)',
    margin: '20px 0',
    position: 'relative',
    overflow: 'hidden',
    zIndex: 1,
    '&::before': {
      content: '""',
      position: 'absolute',
      width: '100%',
      height: '100%',
      borderRadius: '15px',
      background: 'repeating-conic-gradient(from var(--a), #45f3ff 0%, #45f3ff 10%, transparent 10%, transparent 80%, #45f3ff 100%)',
      animation: '$animate 2.5s linear infinite',
      zIndex: -1,
    },
  },
  form: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    background: 'transparent',
    position: 'relative',
    zIndex: 2,
  },
  fileInput: {
    width: '97%',
    margin: '10px 0',
    color: 'white', // White text color for file input
    '& .MuiInputBase-root': {
      color: 'white', // White text color for the input label
    },
  },
  buttonSubmit: {
    marginBottom: 10,
    backgroundColor: '#007bff', // Custom blue color for the submit button
    color: '#fff',
    '&:hover': {
      backgroundColor: '#0056b3', // Darker blue on hover
    },
  },
  buttonClear: {
    backgroundColor: '#ff4d4d', // Custom red color for the clear button
    color: '#fff',
    '&:hover': {
      backgroundColor: '#cc0000', // Darker red on hover
    },
  },
}));
