import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  form: {
    padding: theme.spacing(2),
    display: 'flex',
    flexDirection: 'column',
    gap: theme.spacing(2),
    backgroundColor: '#f7f7f7',
    borderRadius: '15px',
  },
  formTitle: {
    fontFamily: 'Cursive',
    textAlign: 'center',
    color: '#ff5722',
  },
  textField: {
    marginBottom: theme.spacing(2),
  },
  fileInput: {
    width: '97%',
    margin: '10px 0',
  },
  buttonSubmit: {
    backgroundColor: '#4caf50',
    color: '#fff',
  },
}));
