import { makeStyles } from '@material-ui/core/styles';

export default makeStyles({
  media: {
    height: 0,
    paddingTop: '56.25%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    backgroundBlendMode: 'darken',
  },
  card: {
    borderRadius: '15px',
    margin: '15px',
    boxShadow: '0px 5px 15px rgba(0, 0, 0, 0.3)',
  },
  overlay: {
    position: 'absolute',
    top: '20px',
    left: '20px',
    color: 'white',
  },
  overlay2: {
    position: 'absolute',
    top: '20px',
    right: '20px',
    color: 'white',
  },
  details: {
    display: 'flex',
    justifyContent: 'space-between',
    margin: '20px',
  },
  title: {
    padding: '0 16px',
    color: '#ff5722',
  },
  cardActions: {
    padding: '0 16px 8px 16px',
    display: 'flex',
    justifyContent: 'space-between',
  },
  creator: {
    fontWeight: 'bold',
    fontFamily: 'Cursive',
  },
  date: {
    fontSize: '0.85rem',
  },
  tags: {
    color: '#3f51b5',
  },
  likeButton: {
    color: '#ff5722',
  },
  deleteButton: {
    color: '#f44336',
  },
});
