import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
    },
  },
  paper: {
    padding: theme.spacing(2), // Increase padding for a more spacious look
    backgroundColor: '#fff', // Ensure the background is white
    borderRadius: theme.shape.borderRadius, // Rounded corners for the paper
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Add a subtle shadow for the "vacuum" effect
    margin: theme.spacing(3), // Add margin around the paper
  },
  form: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    alignItems: 'center', // Center items vertically as well
    width: '100%',
  },
  fileInput: {
    width: '97%',
    margin: '10px 0',
  },
  buttonSubmit: {
    marginBottom: 10,
    backgroundColor: theme.palette.primary.main, // Use the primary color for the button
    color: '#fff', // Ensure the text is white
    '&:hover': {
      backgroundColor: theme.palette.primary.dark, // Darken the button on hover
    },
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', // Add a subtle shadow to the button
  },
}));
