import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
    const [formData, setFormData] = useState({
        name: '',
        password: '',
        phone: '',
    });
    const [showVerification, setShowVerification] = useState(false);
    const [inputCode, setInputCode] = useState('');
    const [serverCode, setServerCode] = useState(''); // Simulating the code from the server
    const navigate = useNavigate(); // Initialize the useNavigate hook

    useEffect(() => {
        // Check if we should show the verification step
        const verificationStep = localStorage.getItem('showVerification');
        const savedCode = localStorage.getItem('serverCode');
        const savedFormData = JSON.parse(localStorage.getItem('formData'));

        if (savedFormData) {
            setFormData(savedFormData);
        }

        if (verificationStep === 'true') {
            setShowVerification(true);
            setServerCode(savedCode);
        }
    }, []);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:5000/auth', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    name: formData.name,
                    password: formData.password,
                    phone_number: formData.phone,
                }),
            });
            const data = await response.json();
            if (response.ok) {
                console.log('Registration Success:', data);
                const code = data.verificationCode.toString(); // Ensure serverCode is stored as a string
                setServerCode(code); 
                localStorage.setItem('serverCode', code); // Save to localStorage
                setShowVerification(true); // Move to the verification step
                localStorage.setItem('showVerification', 'true'); // Persist the state
                localStorage.setItem('formData', JSON.stringify(formData)); // Save form data
            } else {
                throw new Error(data.message || 'Registration failed');
            }
        } catch (error) {
            console.error('Registration Error:', error);
            alert(error.message);
        }
    };

    const handleGoToBot = (e) => {
        e.preventDefault();
        localStorage.setItem('showVerification', 'true'); // Save verification state before leaving
        localStorage.setItem('formData', JSON.stringify(formData)); // Save form data before leaving
        window.location.href = 'https://t.me/regnodejs_bot';
    };

    const handleVerification = () => {
        if (inputCode === serverCode) {
            alert('Verification successful!');
            localStorage.removeItem('showVerification'); // Clear the verification state
            localStorage.removeItem('formData'); // Clear the saved form data
            navigate('/'); // Navigate to the home page
        } else {
            alert('Incorrect verification code. Please try again.');
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            {!showVerification ? (
                <form onSubmit={handleSubmit} style={formStyle}>
                    {/* Registration form fields */}
                    <h2 style={titleStyle}>REGISTER</h2>
                    <input type="text" name="name" placeholder="Enter your name" value={formData.name} onChange={handleChange} style={inputStyle} />
                    <input type="password" name="password" placeholder="Enter your password" value={formData.password} onChange={handleChange} style={inputStyle} />
                    <input type="tel" name="phone" placeholder="Enter your phone number" value={formData.phone} onChange={handleChange} style={inputStyle} />
                    <button type="submit" style={buttonStyle}>Register</button>
                </form>
            ) : (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <h2 style={titleStyle}>Start to bot:</h2>
                    <input type="text" placeholder="Verification Code" value={inputCode} onChange={(e) => setInputCode(e.target.value)} style={inputStyle} />
                    <div style={{ display: 'flex', gap: '20px' }}>
                        <button onClick={handleGoToBot} style={buttonStyle}>Go to bot</button>
                        <button onClick={handleVerification} style={buttonStyle}>Verify</button>
                    </div>
                </div>
            )}
        </div>
    );
};

const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    width: '300px',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 10px 20px rgba(0, 0, 0, 0.2)',
    border: '1px solid #ccc',
};

const inputStyle = {
    marginBottom: '15px',
    padding: '12px',
    borderRadius: '6px',
    border: '1px solid #ddd',
    fontSize: '16px',
    outline: 'none',
    transition: 'border-color 0.3s',
};

const buttonStyle = {
    padding: '12px',
    borderRadius: '6px',
    backgroundColor: '#28a745',
    color: '#fff',
    border: 'none',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
    textDecoration: 'none',
};

const titleStyle = {
    marginBottom: '20px',
    fontSize: '24px',
    color: '#333',
    textAlign: 'center',
    color: 'white',
};

export default Register;
