const useStyles = makeStyles((theme) => ({
  appBar: {
    borderRadius: 15,
    margin: '30px 0',
    padding: '10px 50px',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
  },
  heading: {
    color: 'rgba(0,183,255, 1)',
  },
  image: {
    marginRight: '15px',
  },
  grow: {
    flexGrow: 1,
  },
  toolbar: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%',
  },
  startSection: {
    display: 'flex',
    alignItems: 'center',
  },
  endSection: {
    display: 'flex',
    alignItems: 'center',
  },
}));
